const apiKey = process.env.REACT_APP_GOOGLE_API_KEY;

function getEstimate(data) {
    //data represents complete state from redux store
    const originCityString = convertCityNameToUsableString(data.startingCity);
    const destinationCityString = convertCityNameToUsableString(data.destinationCity);

    //send req to Google distance matrix api

    return (dispatch) => {
        dispatch({ type: 'START_GOOGLE_MAPS_API_REQ' });
        fetch(`https://maps.googleapis.com/maps/api/distancematrix/json?units=imperial&origins=${originCityString},${data.startingState}&destinations=${destinationCityString},${data.destinationState}&key=${apiKey}`)
            .then(response => response.json())
            .then(data => console.log('fetched google maps data', data))
            .catch(err => console.log('err fetching from google maps', err))
    }
}

function convertCityNameToUsableString(cityName) { 
    let cityWordArr = cityName.split(' ');

    if (cityWordArr.length > 1) {
        return cityWordArr.join('+');
    } else {
        return cityName;
    }
}

export default getEstimate;
